export function Card({ title, value, type, bgColor, index }) {
  const Icon = iconMap[index];

  return (
    <div
      style={{ backgroundColor: bgColor }}
      className="rounded-lg border-[2px] border-solid border-[#F4F2F2]  p-2 shadow-sm"
    >
      <div className="flex items-center justify-between gap-3 p-4">
        <h3 className="ml-2 whitespace-nowrap text-[#00000099] leading-3  text-base font-semibold">
          {title}
        </h3>
        {Icon ? Icon : null}
      </div>
      <p
        className={`
          truncate rounded-xl px-12 py-2  text-[#000000] font-bold text-3xl`}
      >
        {value}
      </p>
    </div>
  );
}
