export default function NavLinks() {
  const pathname = usePathname();

  return (
    <>
      {links.map((link) => {
        const LinkIcon = link.icon;
        return (
          <Link
            key={link.name}
            href={link.href}
            style={{ transition: "all 0.5s ease" }}
            className={clsx(
              "flex md:h-[48px] grow items-center px-6  hover:scale-[1.1] gap-10   p-3 text-sm font-medium   md:flex-none   "
            )}
          >
            {LinkIcon}
            <p className=" select-none text-[rgba(255,255,255,1)] text-xl font-semibold leading-3 whitespace-nowrap hidden md:block">
              {link.name}
            </p>
          </Link>
        );
      })}
    </>
  );
}
